import React from "react";
import { connect } from "react-redux";
import { createIncrementAction } from "../../redux/count_actions";

class Count extends React.Component {
  add = () => {
    createIncrementAction(1);
  };
  render() {
    return (
      <div>
        <h1>当前求和为: {this.props.he}</h1>
        <button onClick={this.add}>求和+1</button>
      </div>
    );
  }
}

export default connect((state) => ({ he: state }), {})(Count);
